import Form from "./Form";

export default function App() {
  return (
    <div className="container">
      <Form />
      <Result />
    </div>
  );
}

export function Button() {
  return (
    <button class="cta-btn">
      <svg
        xmlns="http://www.w3.org/2000/svg"
        width="24"
        height="24"
        fill="none"
        viewBox="0 0 24 24"
      >
        <path
          fill="#133041"
          d="M18.75 2.25H5.25a1.5 1.5 0 0 0-1.5 1.5v16.5a1.5 1.5 0 0 0 1.5 1.5h13.5a1.5 1.5 0 0 0 1.5-1.5V3.75a1.5 1.5 0 0 0-1.5-1.5Zm-10.5 16.5a1.125 1.125 0 1 1 0-2.25 1.125 1.125 0 0 1 0 2.25Zm0-3.75a1.125 1.125 0 1 1 0-2.25 1.125 1.125 0 0 1 0 2.25ZM12 18.75a1.125 1.125 0 1 1 0-2.25 1.125 1.125 0 0 1 0 2.25ZM12 15a1.125 1.125 0 1 1 0-2.25A1.125 1.125 0 0 1 12 15Zm3.75 3.75a1.125 1.125 0 1 1 0-2.25 1.125 1.125 0 0 1 0 2.25Zm0-3.75a1.125 1.125 0 1 1 0-2.25 1.125 1.125 0 0 1 0 2.25Zm1.5-5.25a.75.75 0 0 1-.75.75h-9a.75.75 0 0 1-.75-.75V6a.75.75 0 0 1 .75-.75h9a.75.75 0 0 1 .75.75v3.75Z"
        />
      </svg>
      <span>Calculate Repayments</span>
    </button>
  );
}

export function Input({ id, children, addOn, isFullWidth }) {
  return (
    <div className={`form-group ${isFullWidth ? "" : "half"}`} id={id}>
      <div>
        <label for={id}>{children}</label>
        <input type="text" />
        <span className="input-add-on">{addOn}</span>
      </div>
    </div>
  );
}
export function Option({ children, id }) {
  return (
    <div className="option">
      <input type="radio" name="option" id={id} />
      <label for={id}>
        <span className="circle"></span>
        <span className="option-name">{children}</span>
      </label>
    </div>
  );
}

function Result() {
  return (
    <div className="right">
      <ResultPic />
      <ResultDesc />
      {/* <ResultCard /> */}
    </div>
  );
}

function ResultPic() {
  return (
    <>
      <div class="result-pic">
        <img src="images/illustration-empty.svg" alt="" />
      </div>
    </>
  );
}

function ResultDesc() {
  return (
    <>
      <h3 class="result-title">Results shown here</h3>
      <p class="result-desc">
        Complete the form and click “calculate repayments” to see what your
        monthly repayments would be.
      </p>

      {/* 
      <h3 class="result-title">Your results</h3>
      <p class="result-desc">
        Your results are shown below based on the information you provided. To adjust the results, edit the form and
        click
        “calculate repayments” again.
      </p>
      */}
    </>
  );
}

function ResultCard() {
  return (
    <div class="result-card">
      <h4 class="card-title">Your monthly repayments</h4>
      <div class="card-monthly-price">£1,797.74</div>
      <h4 class="card-title">Total you’ll repay over the term</h4>
      <div class="card-total-price">£539,322.94</div>
    </div>
  );
}
