export default function ResultCard({ amount, term, interest }) {
  const monthlyInterest = interest / 100 / 12;
  const numberOfPayments = term * 12;
  const monthlyRepayment =
    amount *
    ((monthlyInterest * (1 + monthlyInterest) ** numberOfPayments) /
      (1 + monthlyInterest) ** numberOfPayments -
      1);
  const totalRepayment = monthlyRepayment * numberOfPayments;

  return (
    <div class="result-card">
      <h4 class="card-title">Your monthly repayments</h4>
      <div class="card-monthly-price">£{monthlyRepayment}</div>
      <h4 class="card-title">Total you’ll repay over the term</h4>
      <div class="card-total-price">£{totalRepayment}</div>
    </div>
  );
}
