export default function App() {
  return (
    <div className="container">
      <Form />
      <Result />
    </div>
  );
}

function Form() {
  return (
    <div className="left">
      <form action="">
        <Header />
        <Inputs />
        <Options />
        <Button />
      </form>
    </div>
  );
}

function Header() {
  return (
    <div className="header">
      <h1>Mortgage Calculator</h1>
      <button className="reset-btn">Clear All</button>
    </div>
  );
}
function Inputs() {
  return (
    <div className="form-groups">
      <Input />
      <div className="row">
        <Input />
        <Input />
      </div>
    </div>
  );
}
function Options() {}
function Button() {}

function Result() {}

function Input({ id, children, addOn, isFullWidth }) {
  return (
    <div className={`form-group ${isFullWidth ? "" : "half"}`} id={id}>
      <div>
        <label for="amount">{children}</label>
        <input type="text" />
        <span className="input-add-on">{addOn}</span>
      </div>
    </div>
  );
}
