export default function App() {
  return (
    <div className="container">
      <Form />
      <Result />
    </div>
  );
}

function Form() {
  return (
    <div className="left">
      <form action="">
        <Header />
        <Inputs />
        <Options />
        <Button />
      </form>
    </div>
  );
}

function Header() {
  return (
    <div className="header">
      <h1>Mortgage Calculator</h1>
      <button className="reset-btn">Clear All</button>
    </div>
  );
}
function Inputs() {
  return (
    <div className="form-groups">
      <Input id="amount" addOn="Â£" isFullWidth={true}>
        Mortgage Amount
      </Input>
      <div className="row">
        <Input id="term" addOn="years" isFullWidth={false}>
          Mortgage Term
        </Input>
        <Input id="interest-rate" addOn="%" isFullWidth={false}>
          Interest Rate
        </Input>
      </div>
    </div>
  );
}
function Options() {
  return (
    <div className="form-group-options">
      <div>
        <label>Mortgage Type</label>
      </div>
    </div>
  );
}
function Button() {}

function Result() {}

function Input({ id, children, addOn, isFullWidth }) {
  return (
    <div className={`form-group ${isFullWidth ? "" : "half"}`} id={id}>
      <div>
        <label for={id}>{children}</label>
        <input type="text" />
        <span className="input-add-on">{addOn}</span>
      </div>
    </div>
  );
}
function Option({ children, id }) {
  return (
    <div class="option">
      <input type="radio" name="option" id={id} />
      <label for={id}>
        <span class="circle"></span>
        <span class="option-name">{children}</span>
      </label>
    </div>
  );
}
